import React from "react";
import Posts from "../posts/posts";
import axios from "axios";
import { useEffect, useState } from "react";
import "./user.css";

const User = (props) => {
  const [posts, setPosts] = useState([]);
  useEffect(() => {
    let fetchData = async () => {
      let result = await axios.get(
        "https://jsonplaceholder.typicode.com/posts"
      );
      setPosts(result.data);
    };
    fetchData();
  }, []);

  posts.length = 3;
  //   let postUserId = posts.find((post) => post.userId === props.user.id);
  //   console.log(postUserId);

  function openPosts() {
    let dia = document.getElementById(props.user.id);
    if (dia.style.display !== "flex") {
      dia.style.display = "flex";
    } else {
      dia.style.display = "none";
    }
  }

  return (
    <div className="user">
      <h1 className="user-name">{props.user.name}</h1>
      <span className="user-span">Email: {props.user.email}</span>
      <span className="user-span">City: {props.user.address.city}</span>
      <div className="user-block">
        <button className="user-btn" onClick={openPosts}>
          Post
        </button>
        <dialog id={props.user.id} className="div">
          {posts.map((post) => (
            <Posts post={post} />
          ))}
        </dialog>
      </div>
    </div>
  );
};

export default User;
