import React from "react";
import "./user.css";

const User = (props) => {
  function openPosts(id) {
    let block = document.getElementById(id).innerHTML;
    console.log(block);
  }
  return (
    <div className="user">
      <h1 className="user-name">{props.user.name}</h1>
      <span className="user-span">Email: {props.user.email}</span>
      <span className="user-span">City: {props.user.address.city}</span>
      <div id={props.user.id} className="user-block">
        <button onChange={openPosts(props.user.id)}>Post</button>
      </div>
    </div>
  );
};

export default User;
