import "./posts.css";
import React from "react";

const Posts = (props) => {
  function openComents() {
    let dialog = document.getElementById(props.post.id);
    if (dialog.style.display !== "flex") {
      dialog.style.display = "flex";
    } else {
      dialog.style.display = "none";
    }
  }
  return (
    <div className="post">
      <h2 className="post-title">{props.post.title}</h2>
      <span className="post-body">{props.post.body}</span>
      <button className="post-btn" onClick={openComents}>
        Coments
      </button>
    </div>
  );
};

export default Posts;
